import React, { useState } from "react";

function CreateArea(props) {

const [note,setNote]=useState({
    title:"",
    content:""
});

function handleChange(event){
    const {name,value}=event.target;

    setNote(function (prevNote) {
        return {
            ...prevNote,//...prev,value to be added=prev+value ..prev is spread operator
            [name]: value//[name] is not treated as a string
        }
    });
}

function submitNote(event){
props.onAdd(note);
set
event.preventDefault();//This is too prevent page from refrshing
